import{_ as p,o as a,c as e,f as h}from"./app.3e5f729f.js";const r={},d=h('<h1 id="\u300A\u975E\u66B4\u529B\u6C9F\u901A\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u975E\u66B4\u529B\u6C9F\u901A\u300B" aria-hidden="true">#</a> \u300A\u975E\u66B4\u529B\u6C9F\u901A\u300B</h1><h2 id="\u7B80\u8FF0" tabindex="-1"><a class="header-anchor" href="#\u7B80\u8FF0" aria-hidden="true">#</a> \u7B80\u8FF0</h2><p>\u5728\u8BFB\u672C\u4E66\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u80FD\u660E\u663E\u53D1\u73B0\u81EA\u5DF1\u751F\u6D3B\u4E2D\u60C5\u7EEA\u7684\u4E0D\u7A33\u5B9A\uFF0C\u66FE\u7ECF\u8BF4\u8BDD\u3001\u505A\u4E8B\u7684\u65B9\u5F0F\u5B58\u5728\u95EE\u9898\uFF0C\u8BF4\u51FA\u4F24\u4EBA\u7684\u8BDD\u505A\u51FA\u4F24\u4EBA\u7684\u4E8B\u65F6\u60C5\u7EEA\u5DF2\u5728\u6124\u6012\u5D29\u6E83\u7684\u9876\u70B9\u3002</p><p>\u867D\u7136\u8BFB\u5B8C\u4E86\u8FD9\u672C\u4E66\uFF0C\u4F46\u8FD8\u9700\u8981\u4E0B\u610F\u8BC6\u7684\u6267\u884C\u548C\u5927\u91CF\u7684\u5B9E\u8DF5\u5F62\u6210\u4E60\u60EF\u3002\u5BDF\u89C9\u81EA\u5DF1\u7684\u60C5\u7EEA\u662F\u5F88\u5BB9\u6613\u505A\u5230\u7684\uFF0C\u4F46\u8868\u8FBE\u81EA\u5DF1\u7684\u611F\u53D7\u548C\u8BF7\u6C42\uFF0C\u5BF9\u4E8E\u81EA\u5DF1\u5185\u655B\u7684\u6027\u683C\u662F\u6709\u96BE\u5EA6\u7684\uFF0C\u5927\u6982\u662F\u7F9E\u4E8E\u8868\u8FBE\u3002</p><h2 id="\u7B80\u4ECB" tabindex="-1"><a class="header-anchor" href="#\u7B80\u4ECB" aria-hidden="true">#</a> \u7B80\u4ECB</h2><p>\u5E2E\u52A9\u4E2A\u4EBA\u8F7B\u677E\u89E3\u51B3\u95EE\u9898\u7684\u9876\u7EA7\u6C9F\u901A\u672F\u3002\u4EE5\u975E\u66B4\u529B\u6C9F\u901A\u7684\u65B9\u5F0F\uFF0C\u7597\u6108\u5185\u5FC3\u6DF1\u5904\u7684\u9690\u79D8\u4F24\u75DB\uFF0C\u8D85\u8D8A\u4E2A\u4EBA\u5FC3\u667A\u548C\u60C5\u611F\u7684\u5C40\u9650\u6027\uFF0C\u7A81\u7834\u90A3\u4E9B\u5F15\u53D1\u6124\u6012\u3001\u6CAE\u4E27\u3001\u7126\u8651\u7B49\u8D1F\u9762\u60C5\u7EEA\u7684\u601D\u7EF4\u65B9\u5F0F\uFF0C\u7528\u4E0D\u5E26\u4F24\u5BB3\u7684\u65B9\u5F0F\u5316\u89E3\u4EBA\u9645\u95F4\u7684\u51B2\u7A81\uFF0C\u5B66\u4F1A\u5EFA\u7ACB\u548C\u8C10\u7684\u751F\u547D\u4F53\u9A8C\u3002</p><h2 id="\u4F5C\u8005" tabindex="-1"><a class="header-anchor" href="#\u4F5C\u8005" aria-hidden="true">#</a> \u4F5C\u8005</h2><p>\u9A6C\u6B47\u5C14\xB7\u5362\u68EE\u5821\uFF0C\u7531\u4E8E\u5728\u4FC3\u8FDB\u4EBA\u7C7B\u548C\u8C10\u5171\u5904\u65B9\u9762\u7684\u7A81\u51FA\u6210\u5C31\uFF0C2006\u5E74\u4ED6\u83B7\u5F97\u4E86\u5730\u7403\u6751\u57FA\u91D1\u4F1A\u9881\u53D1\u7684\u548C\u5E73\u4E4B\u6865\u5956\u3002\u5362\u68EE\u5821\u535A\u58EB\u65E9\u5E74\u5E08\u4ECE\u5FC3\u7406\u5B66\u5927\u5E08\u5361\u5C14\xB7\u7F57\u6770\u65AF\uFF0C\u540E\u6765\u4ED6\u53D1\u5C55\u51FA\u6781\u5177\u542F\u53D1\u6027\u548C\u5F71\u54CD\u529B\u7684\u975E\u66B4\u529B\u6C9F\u901A\u7684\u539F\u5219\u548C\u65B9\u6CD5\uFF0C\u4E0D\u4EC5\u6559\u4F1A\u4EBA\u4EEC\u5982\u4F55\u4F7F\u4E2A\u4EBA\u751F\u6D3B\u66F4\u52A0\u548C\u8C10\u7F8E\u597D\uFF0C\u540C\u65F6\u89E3\u51B3\u4E86\u4F17\u591A\u4E16\u754C\u8303\u56F4\u5185\u7684\u51B2\u7A81\u548C\u4E89\u7AEF\u3002</p><h2 id="\u4E66\u6458" tabindex="-1"><a class="header-anchor" href="#\u4E66\u6458" aria-hidden="true">#</a> \u4E66\u6458</h2><h3 id="\u63D2\u56FE" tabindex="-1"><a class="header-anchor" href="#\u63D2\u56FE" aria-hidden="true">#</a> \u63D2\u56FE</h3><p>\u8A00\u8BED\u4E0A\u7684\u6307\u8D23\u3001\u5632\u8BBD\u3001\u5426\u5B9A\u3001\u8BF4\u6559\u4EE5\u53CA\u4EFB\u610F\u6253\u65AD\u3001\u62D2\u4E0D\u56DE\u5E94\u3001\u968F\u610F\u51FA\u53E3\u7684\u8BC4\u4EF7\u548C\u7ED3\u8BBA\u7ED9\u6211\u4EEC\u5E26\u6765\u7684\u60C5\u611F\u548C\u7CBE\u795E\u4E0A\u7684\u521B\u4F24\uFF0C\u751A\u81F3\u6BD4\u8089\u4F53\u7684\u4F24\u5BB3\u66F4\u52A0\u4EE4\u4EBA\u75DB\u82E6\u3002\u8FD9\u4E9B\u65E0\u5FC3\u6216\u6709\u610F\u7684\u8BED\u8A00\u66B4\u529B\u8BA9\u4EBA\u4E0E\u4EBA\u53D8\u5F97\u51B7\u6F20\u3001\u9694\u819C\u3001\u654C\u89C6\u3002</p><h3 id="\u8BD1\u5E8F" tabindex="-1"><a class="header-anchor" href="#\u8BD1\u5E8F" aria-hidden="true">#</a> \u8BD1\u5E8F</h3><p>\u4F7F\u7528\u66B4\u529B\u7684\u4EBA\u5176\u5B9E\u662F\u56E0\u4E3A\u4ED6\u4EEC\u5185\u5FC3\u7684\u5B81\u9759\u906D\u5230\u4E86\u7834\u574F\uFF0C\u6240\u4EE5\u4ED6\u4EEC\u624D\u4F1A\u7528\u66B4\u529B\u7684\u65B9\u5F0F\u7EF4\u62A4\u6216\u5BFB\u6C42\u5FC3\u7075\u7684\u548C\u5E73\u3002</p><p>\u4EBA\u4EEC\u5E38\u8BF4\uFF1A\u201C\u7231\u80FD\u4F7F\u5FC3\u7075\u7684\u521B\u4F24\u75CA\u6108\u3002\u201D\u6211\u6DF1\u4FE1\uFF0C\u7231\u7684\u80FD\u529B\u53D6\u51B3\u4E8E\u5BA1\u7F8E\u80FD\u529B\u3002</p><h3 id="\u524D\u8A00" tabindex="-1"><a class="header-anchor" href="#\u524D\u8A00" aria-hidden="true">#</a> \u524D\u8A00</h3><p>\u975E\u66B4\u529B\u751F\u6D3B\u7684\u4E00\u4E2A\u5173\u952E\u5C31\u662F\uFF1A\u611F\u6FC0\u751F\u6D3B\u7684\u8D50\u4E88\uFF0C\u800C\u4E0D\u8D2A\u5FC3\u3002</p><p>\u5982\u679C\u67D0\u4E2A\u884C\u4E3A\u6D89\u53CA\u4F7F\u7528\u6B66\u529B\uFF0C\u5C31\u628A\u5B83\u5F52\u5230\u66B4\u529B\u4E4B\u6811\u201C\u8EAB\u4F53\u7684\u66B4\u529B\u201D\u90A3\u4E00\u8FB9\uFF1B\u5982\u679C\u4E3B\u8981\u9020\u6210\u7CBE\u795E\u4F24\u5BB3\uFF0C\u90A3\u5C31\u5F52\u5230\u66B4\u529B\u4E4B\u6811\u201C\u9690\u853D\u7684\u66B4\u529B\u201D\u90A3\u4E00\u8FB9\u3002</p><p><mark>\u8EAB\u4F53\u7684\u66B4\u529B\uFF1A\u6454\u4E1C\u897F\uFF0C\u6BB4\u6253</mark></p><p><mark>\u7CBE\u795E\u7684\u66B4\u529B\uFF1A\u51B7\u8A00\u51B7\u8BED\uFF0C\u6307\u8D23\uFF0C\u8FB1\u9A82\uFF0CPUA?</mark></p><p>\u975E\u66B4\u529B\u610F\u5473\u7740\u8BA9\u7231\u878D\u5165\u751F\u6D3B\u3002\u8BA9\u5C0A\u91CD\u3001\u7406\u89E3\u3001\u6B23\u8D4F\u3001\u611F\u6FC0\u3001\u6148\u60B2\u548C\u53CB\u60C5\uFF0C\u800C\u975E\u81EA\u79C1\u81EA\u5229\u3001\u8D2A\u5A6A\u3001\u618E\u6068\u3001\u504F\u89C1\u3001\u6000\u7591\u548C\u654C\u610F\uFF0C\u6765\u4E3B\u5BFC\u751F\u6D3B\u3002</p><h3 id="\u7B2C\u4E00\u7AE0-\u8BA9\u7231\u878D\u5165\u751F\u6D3B" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u7AE0-\u8BA9\u7231\u878D\u5165\u751F\u6D3B" aria-hidden="true">#</a> \u7B2C\u4E00\u7AE0 \u8BA9\u7231\u878D\u5165\u751F\u6D3B</h3><p>\u25C6 \u5F15\u8A00</p><p>\u4E5F\u8BB8\u6211\u4EEC\u5E76\u4E0D\u8BA4\u4E3A\u81EA\u5DF1\u7684\u8C08\u8BDD\u65B9\u5F0F\u662F\u201C\u66B4\u529B\u201D\u7684\uFF0C\u4F46\u6211\u4EEC\u7684\u8BED\u8A00\u786E\u5B9E\u5E38\u5E38\u5F15\u53D1\u81EA\u5DF1\u548C\u4ED6\u4EBA\u7684\u75DB\u82E6\u3002</p><p>\u201CNVC\u201D\u6307\u975E\u66B4\u529B\u6C9F\u901A\u6216\u7231\u7684\u8BED\u8A00\u3002</p><p>\u25C6 \u4EBA\u751F\u7684\u6289\u62E9</p><p>\u901A\u8FC7\u5F3A\u8C03\u503E\u542C\u2014\u2014\u503E\u542C\u81EA\u5DF1\u53CA\u4ED6\u4EBA\uFF0C\u975E\u66B4\u529B\u6C9F\u901A\u57F9\u80B2\u5F7C\u6B64\u7684\u5C0A\u91CD\u3001\u5173\u6CE8\u4E0E\u7231\uFF0C\u4F7F\u4EBA\u4E50\u4E8E\u4E92\u52A9\u3002</p><p>\u975E\u66B4\u529B\u6C9F\u901A\u7684\u56DB\u4E2A\u8981\u7D20\uFF1A\u89C2\u5BDF\u3001\u611F\u53D7\u3001\u9700\u8981\u3001\u8BF7\u6C42</p><p>\u25C6 \u975E\u66B4\u529B\u6C9F\u901A\u7684\u8981\u7D20</p><p>\u975E\u66B4\u529B\u6C9F\u901A\u8FC7\u7A0B\uFF1A \u4EC0\u4E48\u662F\u6211\u7684\u89C2\u5BDF\u3001\u6211\u7684\u611F\u53D7\u5982\u4F55\u3001\u54EA\u4E9B\u9700\u8981\uFF08\u6216\u4EF7\u503C\u3001\u613F\u671B\u7B49\uFF09\u5BFC\u81F4\u90A3\u6837\u7684\u611F\u53D7\u3001\u4E3A\u4E86\u6539\u5584\u751F\u6D3B\uFF0C\u6211\u7684\u8BF7\u6C42\u662F\u4EC0\u4E48\u3002</p><p>\u25C6 \u975E\u66B4\u529B\u6C9F\u901A\u7684\u8FD0\u7528</p><p>\u6211\u8BD5\u7740\u4F53\u4F1A\u4ED6\u7684\u611F\u53D7\uFF0C\u8BF4\u51FA\u6211\u7684\u9700\u8981\uFF0C\u542C\u53D6\u4E0D\u4E2D\u542C\u7684\u56DE\u7B54\u3002\u4ED6\u6765\u8FD9\u91CC\u4E0D\u662F\u4E3A\u4E86\u8BA8\u6211\u6B22\u5FC3\uFF0C\u6211\u6765\u4E5F\u4E0D\u662F\u4E3A\u4E86\u4ED6\u7684\u5E78\u798F\u3002\u6211\u4EEC\u5B66\u7740\u6210\u957F\u3001\u63A5\u7EB3\u548C\u7231\uFF0C\u53EA\u6709\u8FD9\u6837\u624D\u80FD\u5404\u5F97\u6240\u613F\u3002</p><p>\u25C6 \u5C0F\u7ED3</p><p>\u975E\u66B4\u529B\u6C9F\u901A\u63D0\u9192\u6211\u4EEC\u4E13\u6CE8\u4E8E\u5F7C\u6B64\u7684\u89C2\u5BDF\u3001\u611F\u53D7\u3001\u9700\u8981\u548C\u8BF7\u6C42\u3002\u5B83\u9F13\u52B1\u503E\u542C\uFF0C\u57F9\u80B2\u5C0A\u91CD\u4E0E\u7231\uFF0C\u4F7F\u6211\u4EEC\u60C5\u610F\u76F8\u901A\uFF0C\u4E50\u4E8E\u4E92\u52A9\u3002</p><h3 id="\u7B2C\u4E8C\u7AE0-\u662F\u4EC0\u4E48\u8499\u853D\u4E86\u7231" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u7AE0-\u662F\u4EC0\u4E48\u8499\u853D\u4E86\u7231" aria-hidden="true">#</a> \u7B2C\u4E8C\u7AE0 \u662F\u4EC0\u4E48\u8499\u853D\u4E86\u7231\uFF1F</h3><p>\u25C6 \u9053\u5FB7\u8BC4\u5224</p><p>\u9053\u5FB7\u8BC4\u5224\u662F\u7528\u9053\u5FB7\u6807\u51C6\u6765\u8BC4\u5224\u4EBA\uFF0C\u5982\u679C\u4E00\u4E2A\u4EBA\u7684\u884C\u4E3A\u4E0D\u7B26\u5408\u6211\u4EEC\u7684\u4EF7\u503C\u89C2\uFF0C\u90A3\u4ED6\u5C31\u88AB\u770B\u4F5C\u662F\u4E0D\u9053\u5FB7\u7684\u6216\u90AA\u6076\u7684\u3002</p><p>\u6279\u8BC4\u3001\u6307\u8D23\u3001\u8FB1\u9A82\u3001\u5F52\u7C7B\u3001\u6BD4\u8F83\u4EE5\u53CA\u8BC4\u8BBA\u7B49\u90FD\u662F\u5728\u8BC4\u5224\u4EBA\u3002</p><p>\u4EF7\u503C\u5224\u65AD\u53CD\u6620\u4E86\u6211\u4EEC\u7684\u4FE1\u5FF5\u2014\u2014\u751F\u547D\u7684\u9700\u8981\u600E\u6837\u624D\u80FD\u5F97\u5230\u6700\u597D\u7684\u6EE1\u8DB3\u3002</p><p>\u66B4\u529B\u7684\u6839\u6E90\u5728\u4E8E\u4EBA\u4EEC\u5FFD\u89C6\u5F7C\u6B64\u7684\u611F\u53D7\u4E0E\u9700\u8981\uFF0C\u800C\u5C06\u51B2\u7A81\u5F52\u548E\u4E8E\u5BF9\u65B9\u2014\u2014\u81F3\u5C11\u5927\u90E8\u5206\u66B4\u529B\u7684\u6839\u6E90\u90FD\u662F\u5982\u6B64\uFF0C\u4E0D\u8BBA\u662F\u8BED\u8A00\u3001\u7CBE\u795E\u6216\u8EAB\u4F53\u7684\u66B4\u529B\uFF0C\u8FD8\u662F\u5BB6\u5EAD\u3001\u90E8\u843D\u4EE5\u53CA\u56FD\u5BB6\u7684\u66B4\u529B\u3002</p><p>\u25C6 \u8FDB\u884C\u6BD4\u8F83</p><p>\u5982\u679C\u771F\u7684\u60F3\u8FC7\u4E0A\u60B2\u60E8\u751F\u6D3B\uFF0C\u5C31\u53BB\u4E0E\u4ED6\u4EBA\u505A\u6BD4\u8F83\u3002</p><p>\u5728\u6BD4\u8F83\u65F6\uFF0C\u6211\u4EEC\u5F00\u59CB\u89C9\u5F97\u81EA\u5DF1\u6D3B\u5F97\u5F88\u60E8\u3002\u6B64\u65F6\uFF0C\u6211\u4EEC\u7684\u5FC3\u60C5\u6781\u4E3A\u4F4E\u843D\u3002</p><p>\u25C6 \u56DE\u907F\u8D23\u4EFB</p><p>\u6211\u4EEC\u53EF\u4EE5\u7528\u8D1F\u8D23\u4EFB\u7684\u8BED\u8A00\u4EE3\u66FF\u56DE\u907F\u8D23\u4EFB\u7684\u8BED\u8A00\u3002</p><p><mark>\u7528 \u201C\u56E0\u4E3A\u6211\u559C\u6B22\uFF08\u8BA8\u538C\uFF09\u2026\u6240\u4EE5\u6211\u2026\u201D \u4EE3\u66FF \u201C\u56E0\u4E3A\u2026\u6211\u4E0D\u5F97\u4E0D\u2026\u201D</mark></p><p>\u4EBA\u7C7B\u7684\u706D\u4EA1\uFF0C\u5374\u662F\u56E0\u4E3A\u73B0\u4EE3\u4EBA\u552F\u552F\u8BFA\u8BFA\u3001\u7F3A\u4E4F\u8D23\u4EFB\u611F\uFF0C\u6BD5\u606D\u6BD5\u656C\u5730\u670D\u4ECE\u5404\u79CD\u547D\u4EE4\u3002</p><p><mark>\u4E0D\u5728\u6C89\u9ED8\u4E2D\u7206\u53D1\uFF0C\u5C31\u5728\u6C89\u9ED8\u4E2D\u706D\u4EA1\u3002</mark></p><p>\u25C6 \u5F3A\u4EBA\u6240\u96BE</p><p>\u6211\u4EEC\u5BF9\u522B\u4EBA\u7684\u8981\u6C42\u5F80\u5F80\u6697\u542B\u7740\u5A01\u80C1\uFF1A\u5982\u679C\u4E0D\u914D\u5408\uFF0C\u4ED6\u4EEC\u5C31\u4F1A\u53D7\u5230\u60E9\u7F5A\u3002</p><p>\u25C6 \u5C0F\u7ED3</p><p>\u4EBA\u5929\u751F\u70ED\u7231\u751F\u547D\uFF0C\u4E50\u4E8E\u4E92\u52A9\u3002\u53EF\u662F\uFF0C\u5F02\u5316\u7684\u6C9F\u901A\u65B9\u5F0F\u4F7F\u6211\u4EEC\u96BE\u4EE5\u4F53\u4F1A\u5230\u5FC3\u4E2D\u7684\u7231\u3002</p><h3 id="\u7B2C\u4E09\u7AE0-\u533A\u5206\u89C2\u5BDF\u548C\u8BC4\u8BBA" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u7AE0-\u533A\u5206\u89C2\u5BDF\u548C\u8BC4\u8BBA" aria-hidden="true">#</a> \u7B2C\u4E09\u7AE0 \u533A\u5206\u89C2\u5BDF\u548C\u8BC4\u8BBA</h3><p>\u5C06\u89C2\u5BDF\u548C\u8BC4\u8BBA\u6DF7\u4E3A\u4E00\u8C08\uFF0C\u4EBA\u4EEC\u5C06\u503E\u5411\u4E8E\u542C\u5230\u6279\u8BC4\uFF0C\u751A\u81F3\u4F1A\u4EA7\u751F\u9006\u53CD\u5FC3\u7406\u3002</p><p><mark>\u8BC4\u8BBA\uFF1A\u4F60\u8FD9\u4EF6\u4E8B\u505A\u7684\u4E0D\u5BF9\uFF0C\u5E94\u8BE5\u600E\u6837\u600E\u6837\uFF1B\u89C2\u5BDF\uFF1A\u8FD9\u4EF6\u4E8B\u8FD8\u53EF\u4EE5\u505A\u7684\u66F4\u597D\uFF0C\u6BD4\u5982\u8FD9\u6837\u5B50\u3002</mark></p><p>\u5982\u679C\u4E0D\u628A\u4E8B\u5B9E\u548C\u610F\u89C1\u6DF7\u4E3A\u4E00\u8C08\uFF0C\u6211\u4EEC\u5C06\u4E0D\u518D\u56F0\u60D1\u3002\u56E0\u4E3A\u4F60\u53EF\u80FD\u65E0\u6240\u8C13\uFF0C\u6211\u4E5F\u60F3\u8BF4\uFF1A\u8FD9\u53EA\u662F\u6211\u7684\u610F\u89C1\u3002\u2014\u2014\u9C81\u601D\xB7\u8D1D\u672C\u6885\u5C14</p><p>\u25C6 \u4EBA\u7C7B\u667A\u529B\u7684\u6700\u9AD8\u5F62\u5F0F</p><p>\u5370\u5EA6\u54F2\u5B66\u5BB6\u514B\u91CC\u5E0C\u90A3\u7A46\u63D0\uFF08J. Krishnamurti\uFF09\u66FE\u7ECF\u8BF4\uFF0C\u201C\u4E0D\u5E26\u8BC4\u8BBA\u7684\u89C2\u5BDF\u662F\u4EBA\u7C7B\u667A\u529B\u7684\u6700\u9AD8\u5F62\u5F0F\u3002\u201D</p><p>\u5BF9\u4E8E\u5927\u591A\u6570\u7684\u4EBA\u6765\u8BF4\uFF0C\u89C2\u5BDF\u4ED6\u4EBA\u53CA\u5176\u884C\u4E3A\uFF0C\u800C\u4E0D\u8BC4\u5224\u3001\u6307\u8D23\u6216\u4EE5\u5176\u4ED6\u65B9\u5F0F\u8FDB\u884C\u5206\u6790\uFF0C\u662F\u96BE\u4EE5\u505A\u5230\u7684\u3002</p><p><mark>\u8BF4\u8D77\u6765\u5BB9\u6613\u505A\u8D77\u6765\u96BE</mark></p><p>\u25C6 \u5C0F\u7ED3</p><p>\u975E\u66B4\u529B\u6C9F\u901A\u7684\u7B2C\u4E00\u4E2A\u8981\u7D20\u662F\u89C2\u5BDF\u3002\u5C06\u89C2\u5BDF\u548C\u8BC4\u8BBA\u6DF7\u4E3A\u4E00\u8C08\uFF0C\u522B\u4EBA\u5C31\u4F1A\u503E\u5411\u4E8E\u542C\u5230\u6279\u8BC4\uFF0C\u5E76\u53CD\u9A73\u6211\u4EEC\u3002\u975E\u66B4\u529B\u6C9F\u901A\u662F\u52A8\u6001\u7684\u8BED\u8A00\uFF0C\u4E0D\u4E3B\u5F20\u7EDD\u5BF9\u5316\u7684\u7ED3\u8BBA\u3002\u5B83\u63D0\u5021\u5728\u7279\u5B9A\u7684\u65F6\u95F4\u548C\u60C5\u5883\u4E2D\u8FDB\u884C\u89C2\u5BDF\uFF0C\u5E76\u6E05\u695A\u5730\u63CF\u8FF0\u89C2\u5BDF\u7ED3\u679C\u3002\u4F8B\u5982\uFF0C\u5B83\u4F1A\u8BF4\u201C\u6B27\u6587\u5728\u8FC7\u53BB\u76845\u573A\u6BD4\u8D5B\u4E2D\u6CA1\u6709\u8FDB\u4E00\u4E2A\u7403\u201D\uFF0C\u800C\u4E0D\u662F\u8BF4\u201C\u6B27\u6587\u662F\u4E2A\u5DEE\u52B2\u7684\u524D\u950B\u201D\u3002</p><h3 id="\u7B2C\u56DB\u7AE0-\u4F53\u4F1A\u548C\u8868\u8FBE\u611F\u53D7" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u56DB\u7AE0-\u4F53\u4F1A\u548C\u8868\u8FBE\u611F\u53D7" aria-hidden="true">#</a> \u7B2C\u56DB\u7AE0 \u4F53\u4F1A\u548C\u8868\u8FBE\u611F\u53D7</h3><p>\u25C6 \u533A\u5206\u611F\u53D7\u548C\u60F3\u6CD5</p><p>\u5728\u975E\u66B4\u529B\u6C9F\u901A\u4E2D\uFF0C\u6211\u4EEC\u6CE8\u610F\u533A\u5206\u611F\u53D7\u548C\u60F3\u6CD5\u3002</p><p>\u5F53\u6211\u4EEC\u8BF4\u201C\u6211\u89C9\u5F97\u201D\uFF0C\u6211\u4EEC\u5E38\u5E38\u5E76\u4E0D\u662F\u5728\u8868\u8FBE\u611F\u53D7\uFF0C\u800C\u662F\u5728\u8868\u8FBE\u60F3\u6CD5\u3002</p><p>\u25C6 \u5EFA\u7ACB\u8868\u8FBE\u611F\u53D7\u7684\u8BCD\u6C47\u8868</p><p>\uFF081\uFF09\u4E0B\u5217\u8BCD\u8BED\u53EF\u7528\u6765\u8868\u8FBE\u6211\u4EEC\u7684\u9700\u8981\u5F97\u5230\u6EE1\u8DB3\u65F6\u7684\u611F\u53D7\uFF1A\u5174\u594B \u559C\u60A6 \u6B23\u559C \u751C\u871C \u7CBE\u529B\u5145\u6C9B \u5174\u9AD8\u91C7\u70C8\u611F\u6FC0 \u611F\u52A8 \u4E50\u89C2 \u81EA\u4FE1 \u632F\u4F5C \u632F\u594B \u5F00\u5FC3\u9AD8\u5174 \u5FEB\u4E50 \u6109\u5FEB \u5E78\u798F \u9676\u9189 \u6EE1\u8DB3 \u6B23\u6170 \u5FC3\u65F7\u795E\u6021\u559C\u51FA\u671B\u5916\u5E73\u9759 \u81EA\u5728 \u8212\u9002 \u653E\u677E \u8E0F\u5B9E \u5B89\u5168 \u6E29\u6696 \u653E\u5FC3 \u65E0\u5FE7\u65E0\u8651</p><p>\uFF082\uFF09\u4E0B\u5217\u8BCD\u8BED\u53EF\u7528\u6765\u8868\u8FBE\u6211\u4EEC\u7684\u9700\u8981\u6CA1\u6709\u5F97\u5230\u6EE1\u8DB3\u65F6\u7684\u611F\u53D7\uFF1A\u5BB3\u6015 \u62C5\u5FC3 \u7126\u8651 \u5FE7\u8651 \u7740\u6025 \u7D27\u5F20 \u5FC3\u795E\u4E0D\u5B81 \u5FC3\u70E6\u610F\u4E71\u5FE7\u4F24 \u6CAE\u4E27 \u7070\u5FC3 \u6C14\u9981 \u6CC4\u6C14 \u7EDD\u671B \u4F24\u611F \u51C4\u51C9 \u60B2\u4F24\u607C\u6012 \u6124\u6012 \u70E6\u607C \u82E6\u607C \u751F\u6C14 \u538C\u70E6 \u4E0D\u6EE1 \u4E0D\u5FEB \u4E0D\u8010\u70E6\u4E0D\u9AD8\u5174\u9707\u60CA \u5931\u671B \u56F0\u60D1 \u832B\u7136 \u5BC2\u5BDE \u5B64\u72EC \u90C1\u95F7 \u96BE\u8FC7 \u60B2\u89C2\u6C89\u91CD \u9EBB\u6728 \u7CBE\u75B2\u529B\u5C3D \u59D4\u9761\u4E0D\u632F \u75B2\u60EB\u4E0D\u582A \u660F\u660F\u6B32\u7761\u65E0\u7CBE\u6253\u91C7\u5C34\u5C2C \u60ED\u6127 \u5185\u759A \u5992\u5FCC \u9057\u61BE \u4E0D\u8212\u670D</p><h3 id="\u7B2C\u4E94\u7AE0-\u611F\u53D7\u7684\u6839\u6E90" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E94\u7AE0-\u611F\u53D7\u7684\u6839\u6E90" aria-hidden="true">#</a> \u7B2C\u4E94\u7AE0 \u611F\u53D7\u7684\u6839\u6E90</h3><p>\u25C6 \u542C\u5230\u4E0D\u4E2D\u542C\u7684\u8BDD\uFF1A\u56DB\u79CD\u9009\u62E9</p><p>\u975E\u66B4\u529B\u6C9F\u901A\u5F3A\u8C03\uFF0C\u611F\u53D7\u7684\u6839\u6E90\u5728\u4E8E\u6211\u4EEC\u81EA\u8EAB\u3002\u6211\u4EEC\u7684\u9700\u8981\u548C\u671F\u5F85\uFF0C\u4EE5\u53CA\u5BF9\u4ED6\u4EBA\u8A00\u884C\u7684\u770B\u6CD5\uFF0C\u5BFC\u81F4\u4E86\u6211\u4EEC\u7684\u611F\u53D7\u3002</p><p>\u542C\u5230\u4E0D\u4E2D\u542C\u7684\u8BDD\u7684\u56DB\u79CD\u9009\u62E9\uFF1A\u8D23\u5907\u81EA\u5DF1\u3001\u6307\u8D23\u4ED6\u4EBA\u3001\u4F53\u4F1A\u81EA\u5DF1\u7684\u611F\u53D7\u548C\u9700\u8981\u3001\u4F53\u4F1A\u4ED6\u4EBA\u7684\u611F\u53D7\u548C\u9700\u8981\u3002</p><p>\u6211\u4EEC\u53EF\u4EE5\u901A\u8FC7\u201C\u6211\uFF08\u611F\u5230\uFF09\u2026\u2026\u56E0\u4E3A\u6211\u2026\u2026\u201D\u8FD9\u79CD\u8868\u8FBE\u65B9\u5F0F\u6765\u8BA4\u8BC6\u611F\u53D7\u4E0E\u81EA\u8EAB\u7684\u5173\u7CFB\u3002</p><p>\u25C6 \u975E\u66B4\u529B\u6C9F\u901A\u9700\u8981\uFF1A\u751F\u547D\u5065\u5EB7\u6210\u957F\u7684\u8981\u7D20</p><p>\u6279\u8BC4\u5F80\u5F80\u6697\u542B\u7740\u671F\u5F85\u3002</p><p>\u5982\u679C\u6211\u4EEC\u901A\u8FC7\u6279\u8BC4\u6765\u63D0\u51FA\u4E3B\u5F20\uFF0C\u4EBA\u4EEC\u7684\u53CD\u5E94\u5E38\u5E38\u662F\u7533\u8FA9\u6216\u53CD\u51FB\u3002\u53CD\u4E4B\uFF0C\u5982\u679C\u6211\u4EEC\u76F4\u63A5\u8BF4\u51FA\u9700\u8981\uFF0C\u5176\u4ED6\u4EBA\u5C31\u8F83\u6709\u53EF\u80FD\u4F5C\u51FA\u79EF\u6781\u7684\u56DE\u5E94\u3002</p><p><mark>\u6BD4\u8D77\u6279\u8BC4\uFF08\u6697\u542B\u5A01\u80C1\uFF09\uFF0C\u76F4\u63A5\u5927\u80C6\u7684\u8BF4\u51FA\u6211\u4EEC\u7684\u611F\u53D7\u548C\u671F\u671B\u66F4\u6709\u5229\u4E8E\u6C9F\u901A\u3002</mark></p><p>\u25C6 \u96BE\u4EE5\u627F\u53D7\u7684\u75DB\u82E6</p><p>\u957F\u671F\u4EE5\u6765\uFF0C\u5987\u5973\u7684\u5F62\u8C61\u548C\u81EA\u6211\u727A\u7272\u8054\u7CFB\u5728\u4E00\u8D77\u3002\u4E00\u65E6\u628A\u7167\u987E\u4ED6\u4EBA\u5F53\u4F5C\u6700\u9AD8\u804C\u8D23\uFF0C\u5979\u4EEC\u4E5F\u5C31\u4F1A\u503E\u5411\u4E8E\u5FFD\u89C6\u4E2A\u4EBA\u7684\u9700\u8981\u3002</p><p>\u5982\u679C\u6211\u4EEC\u4E0D\u770B\u91CD\u81EA\u5DF1\u7684\u9700\u8981\uFF0C\u522B\u4EBA\u53EF\u80FD\u4E5F\u4E0D\u4F1A\u3002\u5B9E\u9645\u4E0A\uFF0C\u5982\u679C\u76F4\u63A5\u8BF4\u51FA\u9700\u8981\uFF0C\u83B7\u5F97\u79EF\u6781\u56DE\u5E94\u7684\u53EF\u80FD\u6027\u5C31\u4F1A\u589E\u52A0\u3002</p><p>\u25C6 \u5C0F\u7ED3</p><p>\u5BF9\u4E8E\u5927\u591A\u6570\u4EBA\u6765\u8BF4\uFF0C\u4E2A\u4EBA\u6210\u957F\u4E00\u822C\u4F1A\u7ECF\u5386\u4E09\u4E2A\u9636\u6BB5\uFF1A</p><p>\uFF081\uFF09\u201C\u60C5\u611F\u7684\u5974\u96B6\u201D\u2014\u2014\u6211\u4EEC\u8BA4\u4E3A\u81EA\u5DF1\u6709\u4E49\u52A1\u4F7F\u4ED6\u4EBA\u5FEB\u4E50\uFF1B</p><p>\uFF082\uFF09\u201C\u9762\u76EE\u53EF\u618E\u201D\u65F6\u671F\u2014\u2014\u6B64\u65F6\uFF0C\u6211\u4EEC\u62D2\u7EDD\u8003\u8651\u4ED6\u4EBA\u7684\u611F\u53D7\u548C\u9700\u8981\uFF1B</p><p>\uFF083\uFF09\u201C\u751F\u6D3B\u7684\u4E3B\u4EBA\u201D\u2014\u2014\u6211\u4EEC\u610F\u8BC6\u5230\uFF0C\u867D\u7136\u6211\u4EEC\u5BF9\u81EA\u5DF1\u7684\u610F\u613F\u3001\u611F\u53D7\u548C\u884C\u52A8\u8D1F\u6709\u5B8C\u5168\u7684\u8D23\u4EFB\uFF0C\u4F46\u65E0\u6CD5\u4E3A\u4ED6\u4EBA\u8D1F\u8D23\u3002\u4E0E\u6B64\u540C\u65F6\uFF0C\u6211\u4EEC\u8FD8\u8BA4\u8BC6\u5230\uFF0C\u6211\u4EEC\u65E0\u6CD5\u727A\u7272\u4ED6\u4EBA\u6765\u6EE1\u8DB3\u81EA\u5DF1\u7684\u9700\u8981\u3002</p><h3 id="\u7B2C\u516D\u7AE0-\u8BF7\u6C42\u5E2E\u52A9" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u516D\u7AE0-\u8BF7\u6C42\u5E2E\u52A9" aria-hidden="true">#</a> \u7B2C\u516D\u7AE0 \u8BF7\u6C42\u5E2E\u52A9</h3><p>\u25C6 \u63D0\u51FA\u5177\u4F53\u7684\u8BF7\u6C42</p><p>\u6211\u4EEC\u63D0\u51FA\u7684\u8BF7\u6C42\u8D8A\u5177\u4F53\u8D8A\u597D\u3002\u5982\u679C\u6211\u4EEC\u7684\u610F\u601D\u542B\u7CCA\u4E0D\u6E05\uFF0C\u522B\u4EBA\u5C31\u96BE\u4EE5\u4E86\u89E3\u6211\u4EEC\u5230\u5E95\u60F3\u8981\u4EC0\u4E48\u3002</p><p>\u25C6 \u660E\u786E\u8C08\u8BDD\u7684\u76EE\u7684</p><p>\u66F4\u4E3A\u5E38\u89C1\u7684\u662F\uFF0C\u6211\u4EEC\u5728\u8BF4\u8BDD\u65F6\uFF0C\u5E76\u4E0D\u77E5\u9053\u81EA\u5DF1\u60F3\u8981\u4EC0\u4E48\u3002\u8868\u9762\u4E0A\uFF0C\u6211\u4EEC\u662F\u5728\u4E0E\u4EBA\u8C08\u8BDD\uFF0C\u5B9E\u9645\u4E0A\uFF0C\u66F4\u50CF\u662F\u81EA\u8BF4\u81EA\u8BDD\u3002\u8FD9\u65F6\uFF0C\u6211\u4EEC\u7684\u8C08\u8BDD\u5BF9\u8C61\u53EF\u80FD\u5C31\u4E0D\u77E5\u9053\u5982\u4F55\u56DE\u5E94\uFF0C\u751A\u81F3\u4F1A\u611F\u5230\u5C40\u4FC3\u4E0D\u5B89\u3002</p><p>\u25C6 \u8BF7\u6C42\u4E0E\u547D\u4EE4</p><p>\u4E00\u65E6\u4EBA\u4EEC\u8BA4\u4E3A\u4E0D\u7B54\u5E94\u6211\u4EEC\u5C31\u4F1A\u53D7\u5230\u8D23\u7F5A\uFF0C\u4ED6\u4EEC\u5C31\u4F1A\u628A\u6211\u4EEC\u7684\u8BF7\u6C42\u770B\u4F5C\u662F\u547D\u4EE4\u3002\u542C\u5230\u547D\u4EE4\u65F6\uFF0C\u4E00\u4E2A\u4EBA\u53EA\u80FD\u770B\u5230\u4E24\u79CD\u9009\u62E9\uFF1A\u670D\u4ECE\u6216\u53CD\u6297\u3002</p><p>\u5982\u4F55\u533A\u5206\u547D\u4EE4\u548C\u8BF7\u6C42\uFF1A\u8BF7\u6C42\u6CA1\u6709\u5F97\u5230\u6EE1\u8DB3\u65F6\uFF0C\u63D0\u51FA\u8BF7\u6C42\u7684\u4EBA\u5982\u679C\u6279\u8BC4\u548C\u6307\u8D23\uFF0C\u90A3\u5C31\u662F\u547D\u4EE4\uFF1B\u5982\u679C\u60F3\u5229\u7528\u5BF9\u65B9\u7684\u5185\u759A\u6765\u8FBE\u5230\u76EE\u7684\uFF0C\u4E5F\u662F\u547D\u4EE4\u3002</p><p>\u25C6 \u975E\u66B4\u529B\u6C9F\u901A\u7684\u76EE\u7684</p><p>\u5982\u679C\u6211\u4EEC\u53EA\u662F\u60F3\u6539\u53D8\u522B\u4EBA\uFF0C\u4EE5\u4F7F\u4ED6\u4EEC\u7684\u884C\u52A8\u7B26\u5408\u6211\u4EEC\u7684\u5229\u76CA\uFF0C\u90A3\u4E48\u975E\u66B4\u529B\u6C9F\u901A\u5E76\u4E0D\u662F\u9002\u5F53\u7684\u5DE5\u5177\u3002\u975E\u66B4\u529B\u6C9F\u901A\u662F\u7528\u6765\u5E2E\u52A9\u6211\u4EEC\u5728\u8BDA\u5B9E\u548C\u503E\u542C\u7684\u57FA\u7840\u4E0A\u4E0E\u4EBA\u8054\u7CFB\u3002\u4F7F\u7528\u975E\u66B4\u529B\u6C9F\u901A\u65F6\uFF0C\u6211\u4EEC\u5E0C\u671B\u4EBA\u4EEC\u7684\u6539\u53D8\u548C\u884C\u52A8\u662F\u51FA\u4E8E\u5BF9\u751F\u547D\u7684\u7231\u3002</p><p>\u25C6 \u5C0F\u7ED3</p><p>\u975E\u66B4\u529B\u6C9F\u901A\u7684\u7B2C\u56DB\u4E2A\u8981\u7D20\u662F\u8BF7\u6C42\u3002\u6211\u4EEC\u544A\u8BC9\u4EBA\u4EEC\uFF0C\u4E3A\u4E86\u6539\u5584\u751F\u6D3B\uFF0C\u6211\u4EEC\u5E0C\u671B\u4ED6\u4EEC\u505A\u4EC0\u4E48\u3002\u6211\u4EEC\u907F\u514D\u4F7F\u7528\u62BD\u8C61\u7684\u8BED\u8A00\uFF0C\u800C\u501F\u52A9\u5177\u4F53\u7684\u63CF\u8FF0\uFF0C\u6765\u63D0\u51FA\u8BF7\u6C42\u3002</p><h3 id="\u7B2C\u4E03\u7AE0-\u7528\u5168\u8EAB\u5FC3\u503E\u542C" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E03\u7AE0-\u7528\u5168\u8EAB\u5FC3\u503E\u542C" aria-hidden="true">#</a> \u7B2C\u4E03\u7AE0 \u7528\u5168\u8EAB\u5FC3\u503E\u542C</h3><p>\u6CD5\u56FD\u4F5C\u5BB6\u897F\u8499\u5A1C\xB7\u8587\u4F9D\uFF08Si-mone Weil\uFF09\u5199\u9053\uFF1A\u201C\u503E\u542C\u4E00\u4E2A\u5904\u4E8E\u75DB\u82E6\u4E2D\u7684\u4EBA\uFF0C\u4E0D\u4EC5\u5341\u5206\u7F55\u89C1\uFF0C\u800C\u4E14\u975E\u5E38\u56F0\u96BE\u3002\u90A3\u7B80\u76F4\u662F\u5947\u8FF9\uFF1B\u90A3\u5C31\u662F\u5947\u8FF9\u3002\u6709\u4E9B\u4EBA\u8BA4\u4E3A\u4ED6\u4EEC\u53EF\u4EE5\u505A\u5230\uFF0C\u5B9E\u9645\u4E0A\uFF0C\u7EDD\u5927\u90E8\u5206\u7684\u4EBA\u8FD8\u4E0D\u5177\u5907\u8FD9\u79CD\u80FD\u529B\u3002\u201D</p><p>\u5728\u975E\u66B4\u529B\u6C9F\u901A\u4E2D\uFF0C\u503E\u542C\u4ED6\u4EBA\u610F\u5473\u7740\uFF0C\u653E\u4E0B\u5DF2\u6709\u7684\u60F3\u6CD5\u548C\u5224\u65AD\uFF0C\u4E00\u5FC3\u4E00\u610F\u5730\u4F53\u4F1A\u4ED6\u4EBA\u3002\u503E\u542C\u7684\u8FD9\u79CD\u54C1\u8D28\u4F53\u73B0\u4E86\u5B83\u4E0E\u7406\u89E3\u4EE5\u53CA\u540C\u60C5\u4E4B\u95F4\u7684\u533A\u522B\u3002</p><p>\u25C6 \u4F53\u4F1A\u4ED6\u4EBA\u7684\u611F\u53D7\u548C\u9700\u8981</p><p>\u4E0D\u8BBA\u522B\u4EBA\u8BF4\u4EC0\u4E48\uFF0C\u6211\u4EEC\u53EA\u542C\u5230\u4ED6\u4EEC\u6B64\u65F6\u6B64\u523B\u7684\uFF08a\uFF09\u89C2\u5BDF\uFF0C\uFF08b\uFF09\u611F\u53D7\uFF0C\uFF08c\uFF09\u9700\u8981\uFF0C\u548C\uFF08d\uFF09\u8BF7\u6C42\u3002</p><p>\u25C6 \u7ED9\u4ED6\u4EBA\u53CD\u9988</p><p>\u4F5C\u5BB6\u7EA6\u745F\u592B\xB7\u574E\u4F2F\uFF08Joseph Campbell\uFF09\u8BF4\u9053\uFF1A\u201C\u4E3A\u4E86\u5E78\u798F\uFF0C\u5FC5\u987B\u628A\u2018\u522B\u4EBA\u600E\u4E48\u770B\u6211\u2019\u8FD9\u4E2A\u95EE\u9898\u653E\u5728\u4E00\u8FB9\u3002\u201D</p><p>\u25C6 \u4FDD\u6301\u5173\u6CE8</p><p>\u5F53\u5BF9\u65B9\u5F97\u5230\u5145\u5206\u7684\u503E\u542C\uFF0C\uFF08a\uFF09\u6211\u4EEC\u5C06\u4F53\u4F1A\u5230\u6C14\u6C1B\u53D8\u5F97\u8F7B\u677E\uFF1B\uFF08b\uFF09\u4ED6\u4F1A\u505C\u6B62\u8C08\u8BDD\u3002</p><p>\u25C6 \u5F53\u6211\u4EEC\u75DB\u82E6\u5F97\u65E0\u6CD5\u503E\u542C</p><p>\u5F53\u6211\u4EEC\u75DB\u82E6\u5F97\u65E0\u6CD5\u503E\u542C\u4ED6\u4EBA\u65F6\uFF0C\u6211\u4EEC\u9700\u8981\uFF081\uFF09\u4F53\u4F1A\u81EA\u5DF1\u7684\u611F\u53D7\u548C\u9700\u8981\uFF082\uFF09\u5927\u58F0\u5730\u63D0\u51FA\u8BF7\u6C42\uFF083\uFF09\u6362\u4E00\u4E2A\u73AF\u5883\u3002</p><h3 id="\u7B2C\u516B\u7AE0-\u503E\u542C\u7684\u529B\u91CF" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u516B\u7AE0-\u503E\u542C\u7684\u529B\u91CF" aria-hidden="true">#</a> \u7B2C\u516B\u7AE0 \u503E\u542C\u7684\u529B\u91CF</h3><p>\u25C6 \u503E\u542C\u4F7F\u8EAB\u5FC3\u75CA\u6108</p><p>\u5982\u679C\u6709\u4EBA\u503E\u542C\u4F60\uFF0C\u4E0D\u5BF9\u4F60\u8BC4\u5934\u8BBA\u8DB3\uFF0C\u4E0D\u66FF\u4F60\u62C5\u60CA\u53D7\u6015\uFF0C\u4E5F\u4E0D\u60F3\u6539\u53D8\u4F60\uFF0C\u8FD9\u591A\u7F8E\u597D\u554A\u2026\u2026\u6BCF\u5F53\u6211\u5F97\u5230\u4EBA\u4EEC\u7684\u503E\u542C\u548C\u7406\u89E3\uFF0C\u6211\u5C31\u53EF\u4EE5\u7528\u65B0\u7684\u773C\u5149\u770B\u4E16\u754C\uFF0C\u5E76\u7EE7\u7EED\u524D\u8FDB\u2026\u2026\u8FD9\u771F\u795E\u5947\u554A\uFF01\u4E00\u65E6\u6709\u4EBA\u503E\u542C\uFF0C\u770B\u8D77\u6765\u65E0\u6CD5\u89E3\u51B3\u7684\u95EE\u9898\u5C31\u6709\u4E86\u89E3\u51B3\u529E\u6CD5\uFF0C\u5343\u5934\u4E07\u7EEA\u7684\u601D\u8DEF\u4E5F\u4F1A\u53D8\u5F97\u6E05\u6670\u8D77\u6765\u3002</p><p>\u25C6 \u503E\u542C\u9884\u9632\u6F5C\u5728\u7684\u66B4\u529B</p><p>\u5728\u4E00\u4E2A\u751F\u6C14\u7684\u4EBA\u9762\u524D\uFF0C\u6C38\u8FDC\u4E0D\u8981\u7528\u2018\u4E0D\u8FC7\u2019\u2018\u53EF\u662F\u2019\u2018\u4F46\u662F\u2019\u4E4B\u7C7B\u7684\u8BCD\u8BED\u3002</p><p>\u25C6 \u5982\u679C\u522B\u4EBA\u8BF4\u201C\u4E0D\uFF01\u201D</p><p>\u5F53\u522B\u4EBA\u8BF4\u201C\u4E0D\u201D\u7684\u65F6\u5019\uFF0C\u6211\u4EEC\u5E38\u5E38\u4F1A\u8BA4\u4E3A\u4ED6\u4EEC\u662F\u5728\u62D2\u7EDD\u6211\u4EEC\u3002\u6709\u65F6\uFF0C\u6211\u4EEC\u751A\u81F3\u8FD8\u4F1A\u89C9\u5F97\u81EA\u5DF1\u53D7\u5230\u4E86\u4F24\u5BB3\u3002\u7136\u800C\uFF0C\u5982\u679C\u6211\u4EEC\u80FD\u591F\u4F53\u4F1A\u4ED6\u4EBA\u7684\u611F\u53D7\u548C\u9700\u8981\uFF0C\u6211\u4EEC\u4E5F\u8BB8\u5C31\u4F1A\u53D1\u73B0\u662F\u4EC0\u4E48\u4F7F\u4ED6\u4EEC\u65E0\u6CD5\u7B54\u5E94\u6211\u4EEC\u7684\u8BF7\u6C42\u3002</p><p>\u25C6 \u5982\u679C\u522B\u4EBA\u4FDD\u6301\u6C89\u9ED8</p><p>\u6709\u7684\u65F6\u5019\uFF0C\u6211\u4EEC\u8BF4\u4E86\u5FC3\u91CC\u8BDD\uFF0C\u5F88\u60F3\u77E5\u9053\u5BF9\u65B9\u7684\u53CD\u5E94\uFF0C\u5374\u53D1\u73B0\u5BF9\u65B9\u4E00\u53E5\u8BDD\u4E5F\u4E0D\u8BF4\u3002\u8FD9\u65F6\uFF0C\u6211\u4EEC\u4E5F\u8BB8\u4F1A\u5F88\u4E0D\u5B89\uFF0C\u5BB9\u6613\u628A\u4E8B\u60C5\u5F80\u574F\u5904\u60F3\u3002\u5728\u522B\u4EBA\u4FDD\u6301\u6C89\u9ED8\u65F6\uFF0C\u6211\u4EEC\u4E00\u822C\u4F1A\u89C9\u5F97\u6709\u4E9B\u522B\u626D\uFF0C\u800C\u5F88\u96BE\u9759\u4E0B\u5FC3\u6765\u4F53\u4F1A\u5BF9\u65B9\u7684\u611F\u53D7\u548C\u9700\u8981\u3002</p><h3 id="\u7B2C\u4E5D\u7AE0-\u7231\u81EA\u5DF1" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E5D\u7AE0-\u7231\u81EA\u5DF1" aria-hidden="true">#</a> \u7B2C\u4E5D\u7AE0 \u7231\u81EA\u5DF1</h3><p>\u975E\u66B4\u529B\u6C9F\u901A\u6700\u91CD\u8981\u7684\u5E94\u7528\u4E5F\u8BB8\u662F\u57F9\u517B\u5BF9\u81EA\u5DF1\u7684\u7231\u3002</p><p>\u25C6 \u5F53\u6211\u4EEC\u7684\u8868\u73B0\u4E0D\u5B8C\u7F8E</p><p>\u6211\u5E0C\u671B\uFF0C\u6211\u4EEC\u7684\u6539\u53D8\u662F\u51FA\u4E8E\u5BF9\u751F\u547D\u7684\u7231\uFF0C\u800C\u4E0D\u662F\u51FA\u4E8E\u7F9E\u6127\u6216\u5185\u759A\u8FD9\u4E9B\u5177\u6709\u8D1F\u9762\u5F71\u54CD\u7684\u5FC3\u7406\u3002</p><p>\u25C6 \u4E3A\u4EC0\u4E48\u4F1A\u81EA\u8D23\uFF1F</p><p>\u4E00\u65E6\u53D1\u73B0\u81EA\u5DF1\u6B63\u5728\u505A\u65E0\u76CA\u7684\u4E8B\u60C5\uFF0C\u6211\u4EEC\u7684\u6311\u6218\u662F\u5982\u4F55\u5BF9\u9700\u8981\u548C\u4EF7\u503C\u89C2\u4FDD\u6301\u6E05\u9192\u7684\u8BA4\u8BC6\uFF0C\u4EE5\u4F7F\u6211\u4EEC\u7684\u8F6C\u53D8\uFF1A\uFF081\uFF09\u7B26\u5408\u6211\u4EEC\u7684\u5FC3\u613F\uFF1B\uFF082\uFF09\u51FA\u4E8E\u5BF9\u81EA\u5DF1\u7684\u5C0A\u91CD\u548C\u7231\u62A4\uFF0C\u800C\u4E0D\u662F\u51FA\u4E8E\u81EA\u6211\u618E\u6068\u3001\u5185\u759A\u6216\u7F9E\u6127\u3002</p><p>\u25C6 \u81EA\u6211\u5BBD\u6055</p><p>\u975E\u66B4\u529B\u6C9F\u901A\u81EA\u6211\u5BBD\u6055\uFF1A\u611F\u5230\u9057\u61BE\u65F6\uFF0C\u6211\u4EEC\u8BD5\u56FE\u4E86\u89E3\u8FC7\u53BB\u7684\u884C\u4E3A\u6240\u8981\u6EE1\u8DB3\u7684\u9700\u8981\u3002</p><p>\u7231\u60DC\u81EA\u5DF1\u7684\u5173\u952E\u4E4B\u4E00\u662F\u540C\u65F6\u5305\u5BB9\u90A3\u4E24\u4E2A\u4E0D\u540C\u7684\u201C\u6211\u201D\uFF1A\u4E00\u65B9\u9762\u5305\u5BB9\u5BF9\u8FC7\u53BB\u7684\u67D0\u79CD\u884C\u4E3A\u611F\u5230\u540E\u6094\u7684\u201C\u6211\u201D\uFF0C\u53E6\u4E00\u65B9\u9762\u5305\u5BB9\u91C7\u53D6\u90A3\u79CD\u884C\u4E3A\u7684\u201C\u6211\u201D\u3002</p><p>\u25C6 \u201C\u4E0D\u8981\u505A\u4EFB\u4F55\u6CA1\u6709\u4E50\u8DA3\u7684\u4E8B\u60C5\uFF01\u201D</p><p>\u8BA9\u6211\u4EEC\u7684\u884C\u52A8\u57FA\u4E8E\u5BF9\u751F\u547D\u7684\u7231\uFF0C\u800C\u975E\u6050\u60E7\u3001\u5185\u759A\u3001\u7F9E\u6127\u6216\u4E49\u52A1\u3002</p><p>\u5982\u679C\u6211\u4EEC\u7684\u884C\u4E3A\u662F\u51FA\u4E8E\u4E49\u52A1\u3001\u804C\u8D23\u3001\u6050\u60E7\u3001\u5185\u759A\u6216\u7F9E\u6127\uFF0C\u90A3\u4E48\uFF0C\u5373\u4F7F\u6709\u610F\u601D\u7684\u4E8B\u60C5\u4E5F\u4F1A\u53D8\u5F97\u67AF\u71E5\u65E0\u5473\u3002</p><p>\u25C6 \u7528\u201C\u9009\u62E9\u505A\u201D\u4EE3\u66FF\u201C\u4E0D\u5F97\u4E0D\u201D</p><p>\u6211\u9009\u62E9\u505A______\u662F\u56E0\u4E3A\u6211\u60F3\u8981______\u3002</p><p>\u25C6 \u6DF1\u5165\u7406\u89E3\u6211\u4EEC\u884C\u4E3A\u7684\u52A8\u673A</p><p>\u4E3A\u4E86\u94B1\u3001\u4ED6\u4EBA\u7684\u8D5E\u540C\uFF0C\u4EE5\u53CA\u51FA\u4E8E\u6050\u60E7\u3001\u7F9E\u6127\u6216\u5185\u759A\u7684\u5FC3\u7406\uFF0C\u4F60\u505A\u4E86\u54EA\u4E9B\u4E8B\u60C5\uFF1F\u60F3\u4E00\u60F3\u4F60\u4E3A\u8FD9\u4E9B\u4E8B\u60C5\u4ED8\u51FA\u7684\u4EE3\u4EF7\u3002</p><p>\u6700\u5371\u9669\u7684\u884C\u4E3A\u4E5F\u8BB8\u662F\u201C\u56E0\u4E3A\u522B\u4EBA\u7684\u8981\u6C42\u201D\u6211\u4EEC\u4E0D\u5F97\u4E0D\u505A\u3002</p><h3 id="\u7B2C\u5341\u7AE0-\u5145\u5206\u8868\u8FBE\u6124\u6012" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u7AE0-\u5145\u5206\u8868\u8FBE\u6124\u6012" aria-hidden="true">#</a> \u7B2C\u5341\u7AE0 \u5145\u5206\u8868\u8FBE\u6124\u6012</h3><p>\u975E\u66B4\u529B\u6C9F\u901A\u5E76\u4E0D\u4E3B\u5F20\u5FFD\u89C6\u6216\u538B\u6291\u6124\u6012\uFF0C\u5B83\u8BA4\u4E3A\uFF0C\u901A\u8FC7\u6DF1\u5165\u5730\u4E86\u89E3\u6124\u6012\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5145\u5206\u8868\u8FBE\u5185\u5FC3\u7684\u6E34\u671B\u3002</p><p>\u25C6 \u4E3A\u4EC0\u4E48\u6211\u4EEC\u4F1A\u751F\u6C14\uFF1F</p><p>\u751F\u6C14\u7684\u539F\u56E0\u5728\u4E8E\u6211\u4EEC\u7684\u60F3\u6CD5\u2014\u2014\u5BF9\u4ED6\u4EBA\u7684\u8BC4\u5224\u548C\u6307\u8D23\u3002</p><p>\u25C6 \u201C\u5408\u7406\u7684\u6124\u6012\u201D\uFF1F</p><p>\u6124\u6012\u662F\u6211\u4EEC\u7684\u601D\u7EF4\u65B9\u5F0F\u9020\u6210\u7684\u3002\u5B83\u7684\u6838\u5FC3\u662F\u5C1A\u672A\u6EE1\u8DB3\u7684\u9700\u8981\u3002</p><p>\u25C6 \u66B4\u529B\u7684\u79CD\u5B50</p><p>\u8868\u8FBE\u6124\u6012\u7684\u6B65\u9AA4\uFF1A1.\u505C\u4E0B\u6765\u547C\u5438\uFF0C2.\u7559\u610F\u6211\u4EEC\u7684\u6307\u8D23\uFF0C3.\u4F53\u4F1A\u6211\u4EEC\u7684\u9700\u8981\uFF0C4.\u8868\u8FBE\u611F\u53D7\u548C\u5C1A\u672A\u6EE1\u8DB3\u7684\u9700\u8981\u3002</p><h3 id="\u7B2C\u5341\u4E00\u7AE0-\u8FD0\u7528\u5F3A\u5236\u529B\u907F\u514D\u4F24\u5BB3" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u4E00\u7AE0-\u8FD0\u7528\u5F3A\u5236\u529B\u907F\u514D\u4F24\u5BB3" aria-hidden="true">#</a> \u7B2C\u5341\u4E00\u7AE0 \u8FD0\u7528\u5F3A\u5236\u529B\u907F\u514D\u4F24\u5BB3</h3><p>\u25C6 \u4F7F\u7528\u5F3A\u5236\u529B\u7684\u76EE\u7684</p><p>\u4F7F\u7528\u9632\u536B\u6027\u7684\u5F3A\u5236\u529B\uFF0C\u662F\u4E3A\u4E86\u4FDD\u62A4\u81EA\u5DF1\u6216\u4ED6\u4EBA\uFF0C\u800C\u4E0D\u662F\u4E3A\u4E86\u60E9\u7F5A\u3001\u7F9E\u8FB1\u6216\u8C34\u8D23\u4ED6\u4EBA\u3002</p><p>\u6B64\u65F6\uFF0C\u6211\u4EEC\u5E0C\u671B\u75DB\u82E6\u80FD\u8BA9\u4ED6\u4EEC\uFF1A\uFF081\uFF09\u610F\u8BC6\u5230\u81EA\u5DF1\u7684\u8FC7\u9519\uFF1B\uFF082\uFF09\u611F\u5230\u61CA\u6094\uFF1B\uFF083\uFF09\u6539\u53D8\u884C\u4E3A\u3002\u7136\u800C\uFF0C\u5728\u5B9E\u9645\u751F\u6D3B\u4E2D\uFF0C\u60E9\u7F5A\u5F80\u5F80\u52A0\u5F3A\u4E86\u5BF9\u65B9\u7684\u654C\u610F\u548C\u62B5\u89E6\u5FC3\u7406\uFF0C\u4F7F\u53CC\u65B9\u7684\u5173\u7CFB\u66F4\u52A0\u758F\u8FDC\u3002</p><p>\u25C6 \u60E9\u7F5A\u7684\u7C7B\u578B</p><p>\u6709\u7684\u65F6\u5019\u5B69\u5B50\u62D2\u7EDD\u505A\u4E00\u4EF6\u5BF9\u4ED6\u4EEC\u6709\u76CA\u7684\u4E8B\u60C5\uFF0C\u53EA\u662F\u56E0\u4E3A\u4ED6\u4EEC\u4E0D\u60F3\u5728\u7236\u6BCD\u7684\u538B\u529B\u9762\u524D\u5C48\u670D\u3002</p><p>\u25C6 \u60E9\u7F5A\u7684\u4EE3\u4EF7</p><p>\u5F53\u6211\u4EEC\u4E3A\u4E86\u56DE\u907F\u60E9\u7F5A\u53BB\u505A\u4E8B\u60C5\u65F6\uFF0C\u6211\u4EEC\u53EF\u80FD\u4F1A\u5FFD\u89C6\u4E8B\u60C5\u672C\u8EAB\u7684\u4EF7\u503C\uFF0C\u800C\u9677\u4E8E\u5BF9\u5931\u8D25\u7684\u5FE7\u8651\u3002</p><h3 id="\u7B2C\u5341\u4E8C\u7AE0-\u91CD\u83B7\u751F\u6D3B\u7684\u70ED\u60C5" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u4E8C\u7AE0-\u91CD\u83B7\u751F\u6D3B\u7684\u70ED\u60C5" aria-hidden="true">#</a> \u7B2C\u5341\u4E8C\u7AE0 \u91CD\u83B7\u751F\u6D3B\u7684\u70ED\u60C5</h3><p>\u25C6 \u503E\u542C\u5185\u5FC3\u7684\u58F0\u97F3</p><p>\u4E00\u822C\u6765\u8BF4\uFF0C\u6211\u4EEC\u7684\u6587\u5316\u503E\u5411\u4E8E\u628A\u4E2A\u4EBA\u9700\u8981\u770B\u4F5C\u662F\u6D88\u6781\u7684\u3001\u5177\u6709\u7834\u574F\u6027\u7684\u3002\u5982\u679C\u4E00\u4E2A\u4EBA\u516C\u5F00\u8868\u8FBE\u81EA\u5DF1\u7684\u9700\u8981\uFF0C\u5C31\u5F88\u53EF\u80FD\u88AB\u770B\u4F5C\u662F\u81EA\u79C1\u7684\u3002</p><p>\u25C6 \u5FC3\u7075\u73AF\u4FDD</p><p>\u4F53\u4F1A\u81EA\u5DF1\u7684\u611F\u53D7\u548C\u9700\u8981\u53EF\u4EE5\u91CA\u653E\u538B\u529B\u3002</p><p>\u25C6 \u7528\u975E\u66B4\u529B\u6C9F\u901A\u4EE3\u66FF\u8BCA\u65AD</p><p>\u4F53\u4F1A\u4ED6\u4EBA\u7684\u611F\u53D7\u548C\u9700\u8981\u4E5F\u53EF\u4EE5\u91CA\u653E\u538B\u529B\u3002</p><p>\u901A\u8FC7\u57F9\u517B\u975E\u66B4\u529B\u6C9F\u901A\u7684\u610F\u8BC6\u548C\u6280\u5DE7\uFF0C\u6211\u4EEC\u5C31\u53EF\u4EE5\u5728\u771F\u8BDA\u3001\u5F00\u653E\u7684\u6C14\u6C1B\u4E2D\u4E0E\u4ED6\u4EBA\u8FDB\u884C\u5E73\u7B49\u7684\u4E92\u52A8\uFF0C\u4ECE\u800C\u5E2E\u52A9\u4ED6\u4EBA\u6446\u8131\u5FC3\u7406\u7684\u56F0\u6270\u3002</p><h3 id="\u7B2C\u5341\u4E09\u7AE0-\u8868\u8FBE\u611F\u6FC0" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u4E09\u7AE0-\u8868\u8FBE\u611F\u6FC0" aria-hidden="true">#</a> \u7B2C\u5341\u4E09\u7AE0 \u8868\u8FBE\u611F\u6FC0</h3><p>\u25C6 \u8D5E\u626C\u7684\u52A8\u673A</p><p>\u7528\u975E\u66B4\u529B\u6C9F\u901A\u7684\u65B9\u5F0F\u8868\u8FBE\u611F\u6FC0\u65F6\uFF0C\u6211\u4EEC\u53EA\u662F\u4E3A\u4E86\u5E86\u795D\u4ED6\u4EBA\u7684\u884C\u4E3A\u63D0\u5347\u4E86\u6211\u4EEC\u7684\u751F\u6D3B\u54C1\u8D28\uFF0C\u800C\u4E0D\u662F\u60F3\u5F97\u5230\u4EFB\u4F55\u56DE\u62A5\u3002</p><p>\u25C6 \u975E\u66B4\u529B\u6C9F\u901A\u8868\u8FBE\u611F\u6FC0\u7684\u65B9\u5F0F</p><p>\u975E\u66B4\u529B\u6C9F\u901A\u8868\u8FBE\u611F\u6FC0\u7684\u65B9\u5F0F\u5305\u542B\u4E09\u4E2A\u90E8\u5206\uFF1A1\uFF0E\u5BF9\u65B9\u505A\u4E86\u4EC0\u4E48\u4E8B\u60C5\u4F7F\u6211\u4EEC\u7684\u751F\u6D3B\u5F97\u5230\u4E86\u6539\u5584\uFF1B2\uFF0E\u6211\u4EEC\u6709\u54EA\u4E9B\u9700\u8981\u5F97\u5230\u4E86\u6EE1\u8DB3\uFF1B3\uFF0E\u6211\u4EEC\u7684\u5FC3\u60C5\u600E\u4E48\u6837\uFF1F</p><p>\u25C6 \u63A5\u53D7\u522B\u4EBA\u7684\u611F\u6FC0</p><p>\u6211\u4EEC\u505A\u4E86\u4EC0\u4E48\u6709\u76CA\u7684\u4E8B\u60C5\uFF0C\u4ED6\u4EEC\u6709\u54EA\u4E9B\u9700\u8981\u5F97\u5230\u4E86\u6EE1\u8DB3\uFF0C\u4EE5\u53CA\u8FD9\u6FC0\u53D1\u4E86\u4ED6\u4EEC\u600E\u6837\u7684\u60C5\u611F\u3002\u6211\u4EEC\u6BCF\u4E2A\u4EBA\u90FD\u6709\u80FD\u529B\u63D0\u5347\u4ED6\u4EBA\u7684\u751F\u6D3B\u54C1\u8D28\u2014\u2014\u8FD9\u662F\u4E00\u4E2A\u503C\u5F97\u94ED\u8BB0\u5728\u5FC3\u7684\u4E8B\u5B9E\u3002</p><p>\u5F53\u6211\u4EEC\u751F\u6D3B\u5728\u5E73\u9759\u3001\u7231\u4E0E\u548C\u8C10\u4E4B\u4E2D\uFF0C\u6211\u4EEC\u4E5F\u5C31\u878D\u4E3A\u4E86\u4E00\u4F53\u3002</p><p>\u5728\u522B\u4EBA\u8868\u8FBE\u611F\u6FC0\u65F6\uFF0C\u4EBA\u4EEC\u901A\u5E38\u6709\u4E24\u79CD\u622A\u7136\u4E0D\u540C\u7684\u53CD\u5E94\u3002\u4E00\u79CD\u662F\u81EA\u6211\u81A8\u80C0\uFF0C\u76F8\u4FE1\u6211\u4EEC\u6BD4\u522B\u4EBA\u4F18\u8D8A\uFF1B\u53E6\u4E00\u79CD\u662F\u5047\u8C26\u865A\uFF0C\u5426\u5B9A\u522B\u4EBA\u7684\u6B23\u8D4F\uFF0C\u8038\u8038\u80A9\u8BF4\uFF1A\u201C\u54E6\uFF0C\u8FD9\u6CA1\u4EC0\u4E48\u3002</p><p>\u25C6 \u5BF9\u611F\u6FC0\u7684\u6E34\u671B</p><p>\u522B\u4EBA\u5BF9\u4F60\u505A\u7684\u4EC0\u4E48\u4E8B\u8868\u8FBE\u611F\u6FC0\uFF0C\u4F60\u4F1A\u6B23\u559C\u82E5\u72C2\uFF1F</p><p>\u25C6 \u5145\u5206\u8868\u8FBE\u611F\u6FC0</p><p>\u5982\u679C\u65E0\u6CD5\u5411\u90A3\u4E9B\u5BF9\u6211\u4EEC\u7684\u4E00\u751F\u6709\u6781\u4E3A\u91CD\u8981\u5F71\u54CD\u7684\u4EBA\u8868\u8FBE\u611F\u6FC0\uFF0C\u6211\u4EEC\u4F1A\u611F\u5230\u591A\u4E48\u60B2\u54C0\u554A\uFF01</p><p>\u6211\u5E38\u5E38\u5047\u5B9A\u4EBA\u4EEC\u5DF2\u7ECF\u77E5\u9053\u6211\u7684\u60C5\u610F\uFF0C\u7136\u800C\uFF0C\u8FD9\u662F\u60F3\u5F53\u7136\u3002\u6B64\u5916\uFF0C\u5373\u4F7F\u4EBA\u4EEC\u5728\u542C\u5230\u611F\u6FC0\u65F6\u4F1A\u611F\u5230\u5C34\u5C2C\uFF0C\u4ED6\u4EEC\u4E5F\u4F1A\u60F3\u542C\u5230\u660E\u786E\u7684\u8868\u8FBE\u3002</p><p>\u4E00\u4EF6\u503C\u5F97\u505A\u7684\u4E8B\u60C5\u5373\u4F7F\u505A\u5F97\u4E0D\u600E\u4E48\u6837\u4E5F\u662F\u503C\u5F97\u7684\uFF01</p><p>\u25C6 \u5C0F\u7ED3</p><p>\u975E\u66B4\u529B\u6C9F\u901A\u9F13\u52B1\u6211\u4EEC\u5145\u5206\u8868\u8FBE\u611F\u6FC0\u3002\u5728\u8868\u8FBE\u611F\u6FC0\u65F6\uFF0C\u6211\u4EEC\u8BF4\u51FA\uFF1A\uFF081\uFF09\u5BF9\u6211\u4EEC\u6709\u76CA\u7684\u884C\u4E3A\uFF1B\uFF082\uFF09\u6211\u4EEC\u7684\u54EA\u4E9B\u9700\u8981\u5F97\u5230\u4E86\u6EE1\u8DB3\uFF1B\uFF083\uFF09\u6211\u4EEC\u7684\u9700\u8981\u5F97\u5230\u6EE1\u8DB3\u540E\uFF0C\u6211\u4EEC\u662F\u4EC0\u4E48\u6837\u7684\u5FC3\u60C5\u3002</p><h3 id="\u9644\u5F55-\u975E\u66B4\u529B\u6C9F\u901A\u6A21\u5F0F-\u{1F525}" tabindex="-1"><a class="header-anchor" href="#\u9644\u5F55-\u975E\u66B4\u529B\u6C9F\u901A\u6A21\u5F0F-\u{1F525}" aria-hidden="true">#</a> \u9644\u5F55\uFF1A\u975E\u66B4\u529B\u6C9F\u901A\u6A21\u5F0F \u{1F525}</h3><p>\u25C6 \u8BDA\u5B9E\u7684\u8868\u8FBE\u81EA\u5DF1</p><p>\u89C2\u5BDF\uFF1A\u5F53\u6211\u770B\u5230\u3001\u542C\u5230\u3001\u60F3\u5230\u6211\u770B\u5230\u7684/\u542C\u5230\u7684/\u60F3\u5230\u7684\u2026\u2026</p><p>\u611F\u53D7\uFF1A\u8FD9\u8BA9\u6211\u611F\u5230\u2026\u2026</p><p>\u9700\u8981\uFF1A\u56E0\u4E3A\u6211\u9700\u8981\u2026\u2026</p><p>\u8BF7\u6C42\uFF1A\u4F60\u662F\u5426\u613F\u610F\u2026\u2026\uFF1F</p><p>\u25C6 \u5173\u5207\u7684\u503E\u542C\u4ED6\u4EBA</p><p>\u89C2\u5BDF\uFF1A\u5F53\u4F60\u770B\u5230\u3001\u542C\u5230\u3001\u60F3\u5230\u6211\u770B\u5230\u7684/\u542C\u5230\u7684/\u60F3\u5230\u7684\u2026\u2026</p><p>\u611F\u53D7\uFF1A\u4F60\u611F\u5230\u2026\u2026\u5417\uFF1F</p><p>\u9700\u8981\uFF1A\u56E0\u4E3A\u4F60\u9700\u8981\u2026\u2026</p><p>\u8BF7\u6C42\uFF1A\u6240\u4EE5\u4F60\u5E0C\u671B\u2026\u2026</p>',186),i=[d];function n(t,c){return a(),e("div",null,i)}const o=p(r,[["render",n],["__file","20210604\uFF5C\u300A\u975E\u66B4\u529B\u6C9F\u901A\u300B.html.vue"]]);export{o as default};
