import{_ as i,o as l,c as a,f as e}from"./app.140f40fb.js";const h={},r=e('<h1 id="《国宝档案・御膳房》" tabindex="-1"><a class="header-anchor" href="#《国宝档案・御膳房》" aria-hidden="true">#</a> 《国宝档案・御膳房》</h1><h2 id="康熙的节俭" tabindex="-1"><a class="header-anchor" href="#康熙的节俭" aria-hidden="true">#</a> 康熙的节俭</h2><ul><li>京城蜜香居，百味香包子，康熙微服私访，钦赐牌匾百味斋，王公贵族浪费。</li><li>皇帝吃饭称为用膳，进膳。</li><li>清朝皇帝一般每餐二十道菜，康熙最爱吃八宝豆腐，有时一天只吃一次饭。</li><li>清代斗彩花鸟纹碗，康熙使用的餐具，现藏故宫博物院。</li></ul><h2 id="御厨张东官" tabindex="-1"><a class="header-anchor" href="#御厨张东官" aria-hidden="true">#</a> 御厨张东官</h2><ul><li>乾隆皇帝、张东官</li><li>乾隆四次南巡，张东官，糖醋樱桃肉一品，从苏菜厨艺成为御厨，北京卤煮火烧和苏灶。</li><li>乾隆六次南巡，因张东官年老腿脚不便，遣其回苏州养老。</li><li>清代腰牌，木质。</li></ul><h2 id="慈禧的美味" tabindex="-1"><a class="header-anchor" href="#慈禧的美味" aria-hidden="true">#</a> 慈禧的美味</h2><ul><li>花园吃烧烤</li><li>清光绪十八年颐和园，慈禧年老身体变差，吃不了烧猪皮，后发明新菜烧银芽，吃肉不见肉。餐具奢侈，每日百余道菜，点心四五十道。</li><li>德龄公主，慈禧太后，奉天之行。</li><li>1901年，弃京西逃，李莲英，腊驴腿，搜刮民脂民膏。</li><li>三镶银象牙箸，清慈禧用，现藏故宫博物院。</li></ul><h2 id="国宴上的烦恼" tabindex="-1"><a class="header-anchor" href="#国宴上的烦恼" aria-hidden="true">#</a> 国宴上的烦恼</h2><ul><li>清朝国宴，乾隆二十五年，香妃。</li><li>良乡犒劳战士，丰泽园(北京中南海瀛台北面)举办国宴。</li><li>进茶赐茶，进酒赐酒，进膳赐膳。</li><li>千叟宴</li><li>银甲子万年字元宝式火锅，清代，藏于故宫博物院。</li></ul><h2 id="真假满汉全席" tabindex="-1"><a class="header-anchor" href="#真假满汉全席" aria-hidden="true">#</a> 真假满汉全席</h2><ul><li>曹雪芹接待康熙</li><li>尹继善，接待乾隆一次、二次南巡。淮扬菜。</li><li>满汉全席，满族和汉族官员一起吃饭。</li><li>文思豆腐，天宁寺，文思和尚。</li><li>乾隆皇帝《平安春信图》现藏故宫博物院</li></ul><h2 id="溥仪和西餐" tabindex="-1"><a class="header-anchor" href="#溥仪和西餐" aria-hidden="true">#</a> 溥仪和西餐</h2><ul><li>最后一位皇帝，三岁登基，做了十六年皇帝。</li><li>袁世凯优待条件冯玉祥赶皇帝出宫</li><li>早膳26道</li><li>溥仪14岁，庄士敦，生活西化。</li><li>溥仪、婉蓉、文绣</li><li>白地描金红云龙盘，清宣统年。</li><li>赤金錾花餐具，清溥仪使用过。</li></ul><h2 id="天价片汤和鸡蛋" tabindex="-1"><a class="header-anchor" href="#天价片汤和鸡蛋" aria-hidden="true">#</a> 天价片汤和鸡蛋</h2><ul><li>内务府，管御膳房，但一个鸡蛋报价十两银子(今两千多元)，欺上瞒下。</li><li>道光款绿彩云龙纹碗，清代。</li></ul><h2 id="皇帝的小吃" tabindex="-1"><a class="header-anchor" href="#皇帝的小吃" aria-hidden="true">#</a> 皇帝的小吃</h2><ul><li>谁也不是吃素的</li><li>白煮肉，祭祀食品。吃不完，赏给下人，和顺居。</li><li>砂锅白肉</li><li>北京豆汁，从民间进入朝廷，豆汁夏季解暑提神。</li><li>炒麻豆腐，青韭，味串，慈禧年幼时常吃，贫民菜肴。</li><li>碰头食，在哪遇见在哪吃。</li><li>豌豆黄，驴打滚。</li><li>坤宁宫铁锅，北京故宫博物院。</li></ul><h2 id="皇帝的团圆饭" tabindex="-1"><a class="header-anchor" href="#皇帝的团圆饭" aria-hidden="true">#</a> 皇帝的团圆饭</h2><ul><li>甄嬛传演绎的雍正皇帝团圆饭不太平。</li><li>乾清宫</li><li>乾隆、颖妃、容妃等，皇帝和妃嫔的团圆饭。</li><li>元旦开笔</li><li>乾隆御用金瓯永固杯，现藏故宫博物院。</li></ul>',19),d=[r];function n(t,c){return l(),a("div",null,d)}const u=i(h,[["render",n],["__file","20210621｜国宝档案・御膳房.html.vue"]]);export{u as default};
